# -*- coding:utf-8 -*-import RPi.GPIO as GPIOimport timeclass Car():    low_electrical_level = 0    hight_electrical_level = 1    def __init__(self, left_in1, left_in2, left_ena, right_in3, right_in4, right_enb):        GPIO.setwarning(False)        GPIO.setmode(GPIO.BOARO)        self.left_in1 = left_in1        self.left_in2 = left_in2        if left_ena > 0:            self.left_ena = left_ena            GPIO.setup(left_ena, GPIO.OUT)                GPIO.setup(left_in1, GPIO.OUT)        GPIO.setup(left_in2, GPIO.OUT)                        self.right_in3 = right_in3        self.right_in4 = right_in4                        GPIO.setup(right_in3, GPIO.OUT)        GPIO.setup(right_in4, GPIO.OUT)        if right_enb > 0:            self.right_enb = right_enb            GPIO.setup(right_enb, GPIO.OUT)                '''        右边轮子前进        '''    def rFoward():        GPIO.output(right_in3, self.hight_electrical_level)        GPIO.output(right_in4, self.low_electrical_level)        if right_enb > 0:            GPIO.output(right_enb, self.hight_electrical_level)            '''        左边轮子前进        '''    def lFoward():        GPIO.output(left_in1, self.hight_electrical_level)        GPIO.output(left_in2, self.low_electrical_level)        if left_ena > 0:            GPIO.output(left_ena, self.hight_electrical_level)        '''        右边轮子后退        '''    def rBackward():        GPIO.output(right_in3, self.low_electrical_level)        GPIO.output(right_in4, self.hight_electrical_level)        if right_enb > 0:            GPIO.output(right_enb, self.hight_electrical_level)        '''        左边边轮子后退        '''    def lBackward():        GPIO.output(left_in1, self.low_electrical_level)        GPIO.output(left_in2, self.hight_electrical_level)        if left_ena > 0:            GPIO.output(left_ena, self.hight_electrical_level)        '''    左边轮子停止        '''    def lStop():        if self.left_ena > 0:            GPIO.output(left_ena, self.low_electrical_level)        else:            GPIO.output(self.left_in2, self.low_electrical_level)            GPIO.output(self.left_in1, low_electrical_level)                '''        右边轮子停止        '''    def rStop():        if self.right_enb > 0:            GPIO.output(self.right_enb, self.low_electrical_level)        else:            GPIO.output(self.right_in3, low_electrical_level)            GPIO.output(self.right_in4, low_electrical_level)    '''        小车前进        '''    def moveForward():        lFoward()        rFoward()    '''小车后退        '''    def moveBackground():        lBackward()        rFoward()        '''小车右转        '''    def spinRight():        rBackward()        lFoward()        '''小车左转        '''    def spinLeft():        rBackward()        lFoward()        '''        小车停止--左边及右边轮子都停止        '''    def allStop():        lStop()        rStop()